$(function(){
	if( ('sessionStorage' in window) && (window.sessionStorage !== null) ) {
		if( window.sessionStorage.getItem('modalFlg') === null ) {
			window.sessionStorage.setItem('modalFlg', 'false');
		}
		if ( window.sessionStorage.getItem('modalFlg') === 'false' ) {
			$('#modal_overlay').css('display', 'block');
		}
	}

	$('#modal_inner .close, #modal_inner a, #modal_hidden').on('click', function(){
		$('#modal_overlay').fadeOut();

		if( ('sessionStorage' in window) && (window.sessionStorage !== null) ) {
			window.sessionStorage.setItem('modalFlg', 'true');
		}
		e.stopPropagation();
	});
})